" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pretibial myxedema in autoimmune thyroid disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/50/1828/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/50/1828/contributors\">",
"     Terry F Davies, MD, FRCP, FACE",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/50/1828/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/50/1828/contributors\">",
"     Douglas S Ross, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/50/1828/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/50/1828/contributors\">",
"     Jean E Mulder, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
