" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Acute mountain sickness and high altitude cerebral edema",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/6/1130/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/6/1130/contributors\">",
"     Scott A Gallagher, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/6/1130/contributors\">",
"     Peter Hackett, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/6/1130/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/6/1130/contributors\">",
"     Daniel F Danzl, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/6/1130/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/6/1130/contributors\">",
"     Jonathan Grayzel, MD, FAAEM",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
