var title_f1_60_1996="Imm and nonimm anaphylx";
var content_f1_60_1996=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F57346&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F57346&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Mechanisms of anaphylaxis and pseudoallergic syndromes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Proposed mechanism(s)",
"      </td>",
"      <td class=\"subtitle1\">",
"       Characteristics",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       <strong>",
"        IgE-mediated activation of mast cells and basophils",
"       </strong>",
"      </td>",
"      <td rowspan=\"2\">",
"       <p>",
"        &bull; Can be evaluated with skin test and/or in-vitro tests for allergen-specific IgE",
"       </p>",
"       <p>",
"        &bull; Usually requires prior exposure to allergen",
"       </p>",
"       <p>",
"        &bull; Potentially becomes more severe with repeat exposures",
"       </p>",
"       <p>",
"        &bull; Amenable to desensitization in some situations",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Examples: reactions to penicillin and other beta lactams, some neuromuscular blocking agents",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       <strong>",
"        Immunologic mechanisms",
"        <em>",
"         NOT",
"        </em>",
"        involving IgE",
"       </strong>",
"      </td>",
"      <td rowspan=\"2\">",
"       <p>",
"        &bull; Formerly called anaphylactoid reactions",
"       </p>",
"       <p>",
"        &bull; Severity tends to remain constant with repeat exposures",
"       </p>",
"       <p>",
"        &bull; Skin testing and in-vitro testing are",
"        <strong>",
"         NOT",
"        </strong>",
"        useful",
"       </p>",
"       <p>",
"        &bull; Pretreatment with glucocorticoids and/or antihistamines may be helpful",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Examples: reactions mediated by IgG or IgM, antigen-antibody complexes, and/or complement proteins",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Direct (non-immunologic) activation of mast cells and basophils",
"       </strong>",
"      </td>",
"      <td rowspan=\"2\">",
"       <p>",
"        &bull; Severity tends to remain constant with repeat exposures",
"       </p>",
"       <p>",
"        &bull; Skin testing and in-vitro testing are",
"        <strong>",
"         NOT",
"        </strong>",
"        useful",
"       </p>",
"       <p>",
"        &bull; Pretreatment with glucocorticoids and/or antihistamines may be helpful",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Examples: reactions to radiocontrast agents, opioids, and some neuromuscular blocking agents",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
