var title_f1_60_1997="IV medications for pediatric hypertensive emergencies";
var content_f1_60_1997=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F80380&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F80380&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Intravenous drugs for treatment of hypertensive emergencies and urgencies in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Drug*",
"      </td>",
"      <td class=\"subtitle1\">",
"       Route",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dose range&bull;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Onset of action",
"      </td>",
"      <td class=\"subtitle1\">",
"       Duration of action",
"      </td>",
"      <td class=\"subtitle1\">",
"       Mechanism of action",
"      </td>",
"      <td class=\"subtitle1\">",
"       Comments&Delta;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Labetalol",
"       </strong>",
"      </td>",
"      <td>",
"       Intravenous infusion or bolus",
"      </td>",
"      <td>",
"       <p>",
"        Bolus: 0.2- 1 mg/kg up to 40 mg/dose",
"       </p>",
"       <p>",
"        Infusion: 0.25-3 mg/kg/hr",
"       </p>",
"      </td>",
"      <td>",
"       2-5 min",
"      </td>",
"      <td>",
"       2-6 hr",
"      </td>",
"      <td>",
"       &alpha;- and",
"       <sub>",
"        &beta;",
"       </sub>",
"       - adrenergic blocker",
"      </td>",
"      <td>",
"       Contraindicated in asthma, BPD, HF and may mask symptoms of hypoglycemia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Nicardipine",
"       </strong>",
"      </td>",
"      <td>",
"       Intravenous infusion",
"      </td>",
"      <td>",
"       0.5- 4 mcg/kg/min",
"      </td>",
"      <td>",
"       2-5 min",
"      </td>",
"      <td>",
"       30 min - 4 hr (increases with time of infusion)",
"      </td>",
"      <td>",
"       Calcium channel blocker",
"      </td>",
"      <td>",
"       May cause reflex tachycardia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hydralazine",
"      </td>",
"      <td>",
"       Intravenous bolus",
"      </td>",
"      <td>",
"       <p>",
"        IV: 0.2-0.6 mg/kg",
"       </p>",
"       <p>",
"        Maximum single dose: 20 mg",
"       </p>",
"      </td>",
"      <td>",
"       10 min (max effect may take up to 80 min)",
"      </td>",
"      <td>",
"       4-6 hours",
"      </td>",
"      <td>",
"       Direct vasodilator",
"      </td>",
"      <td>",
"       <p>",
"        May cause reflex tachycardia",
"       </p>",
"       <p>",
"        Variable response with potential for prolonged hypotension",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Esmolol",
"      </td>",
"      <td>",
"       Intravenous infusion",
"      </td>",
"      <td>",
"       100-500 mcg/kg loading dose then 100-500 mcg/kg/min",
"      </td>",
"      <td>",
"       Immediate",
"      </td>",
"      <td>",
"       10-30 min",
"      </td>",
"      <td>",
"       <sub>",
"        &beta;",
"       </sub>",
"       - adrenergic blocker",
"      </td>",
"      <td>",
"       Contraindicated in asthma, BPD, HF and may cause profound bradycardia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Fenoldopam",
"      </td>",
"      <td>",
"       Intravenous infusion",
"      </td>",
"      <td>",
"       0.1-2 &micro;g/kg/min",
"      </td>",
"      <td>",
"       5-40 min",
"      </td>",
"      <td>",
"       60 min",
"      </td>",
"      <td>",
"       Peripheral dopamine receptor agonist",
"      </td>",
"      <td>",
"       Limited experience in children",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Nitroprusside",
"      </td>",
"      <td>",
"       Intravenous infusion",
"      </td>",
"      <td>",
"       0.5-10 mcg/kg/min",
"      </td>",
"      <td>",
"       Seconds",
"      </td>",
"      <td>",
"       Effect requires continuous infusion",
"      </td>",
"      <td>",
"       Venodilator with some arteriolar dilation",
"      </td>",
"      <td>",
"       <p>",
"        Monitor cyanide levels with prolonged (&gt;72 hr) use or coadminister sodium thiosulfate",
"       </p>",
"       <p>",
"        May increase ICP",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ICP: intracranial pressure; BPD: bronchopulmonary dysplasia; HF: heart failure.",
"     <br>",
"      * Bolded medications are most commonly used for hypertensive emergencies in children.",
"      <br>",
"       &bull; See text for specific drug information about starting dose and recommended titration intervals.",
"       <br>",
"        &Delta; All agents may cause excessive hypotension.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Flynn, JT, Tullus, K. Severe hypertension in children and adolescents: pathophysiology and treatment. Pediatr Nephrol 2009; 24:1101.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
