" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Performance of prehospital fibrinolysis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
