var title_f10_11_10428="ACAAI ACOG chronic asthma preg";
var content_f10_11_10428=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F55513&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F55513&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Preferred pharmacologic step therapy of asthma during pregnancy: NAEPP update 2004",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Step therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild intermittent",
"       </td>",
"       <td>",
"        Inhaled short-acting beta2 agonist* as needed (for all categories)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Mild persistent",
"       </td>",
"       <td>",
"        Low dose inhaled glucocorticoid",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Moderate persistent",
"       </td>",
"       <td>",
"        Medium dose inhaled glucocorticoid",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low dose inhaled glucocorticoid",
"        <sup>",
"         &bull;",
"        </sup>",
"        plus long-acting beta agonist",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Medium dose inhaled glucocorticoid",
"        <sup>",
"         &bull;",
"        </sup>",
"        plus long-acting beta agonist",
"        <sup>",
"         &Delta;",
"        </sup>",
"        , if needed",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Severe persistent",
"       </td>",
"       <td>",
"        High dose inhaled glucocorticoid",
"        <sup>",
"         &bull;",
"        </sup>",
"        plus long-acting beta agonist",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prednisone if needed",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Albuterol is preferred inhaled short acting beta2 agonist during pregnancy.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Budesonide is preferred inhaled corticosteroid during pregnancy.",
"      <br>",
"       &Delta; Salmeterol is preferred long-acting beta agonist during pregnancy.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Based on data from: Quick Reference. NAEPP Expert Panel Report Managing Asthma During Pregnancy: Recommendations for Pharmacologic Treatment-Update 2004. US Dept of Health and Human Services, Bethesda, MD. NIH Publication No. 04-5246, March, 2004.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
