var title_f10_14_10475="Predispositions for aspiration";
var content_f10_14_10475=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F56501&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F56501&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Conditions that predispose to aspiration",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Altered consciousness",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Alcoholism, seizures, cerebrovascular accident, head trauma, general anesthesia, drug overdose",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Dysphagia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Esophageal disorders including stricture, neoplasm, diverticula, tracheoesophageal fistula, incompetent cardiac sphincter",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Neurologic disorder",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Multiple sclerosis, Parkinson disease, myasthenia gravis, pseudobulbar palsy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Mechanical disruption of the usual defense barriers",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Nasogastric tube, endotracheal intubation, tracheostomy, upper gastrointestinal endoscopy, bronchoscopy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Other",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Protracted vomiting, gastric outlet obstruction, large-volume nasogastric tube feedings, pharyngeal anesthesia, general debility, recumbent position",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
