" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Acute respiratory distress syndrome: Clinical features and diagnosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/16/10505/contributors\">",
