" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Body piercing in adolescents and young adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/2/10282/contributors\">",
