" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Staging and prognostic studies in multiple myeloma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/23/10618/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/23/10618/contributors\">",
"     S Vincent Rajkumar, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/23/10618/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/23/10618/contributors\">",
"     Robert A Kyle, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/23/10618/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/23/10618/contributors\">",
"     Rebecca F Connor, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
