" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Amaurosis fugax (transient monocular or binocular visual loss)",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/26/10666/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/26/10666/contributors\">",
