var title_f10_3_10301="Monitoring STI and OI in adolescent HIV";
var content_f10_3_10301=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F54700&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F54700&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Monitoring for sexually transmitted and opportunistic infections in adolescents with HIV infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommended frequency/comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Toxoplasmosis serologies",
"       </td>",
"       <td>",
"        At initial diagnosis; also may be indicated in patients with initial negative serology if CD4 drops below 100 cells/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        RPR or VDRL*",
"       </td>",
"       <td>",
"        At least yearly; in areas highly endemic for syphilis or high risk populations (ie, MSM) every 6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gonorrhea/chlamydia screening*",
"       </td>",
"       <td>",
"        Every 6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PAP smear (females)",
"        <sup>",
"         &bull;",
"        </sup>",
"        /anal cytology (males)",
"       </td>",
"       <td>",
"        Every 6 months initially, if negative x 2, annually",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tuberculosis screening",
"       </td>",
"       <td>",
"        Annually: TST or IGRA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatitis serologies",
"       </td>",
"       <td>",
"        <p>",
"         Annually: anti-HCV to screen for new infection.",
"        </p>",
"        <p>",
"         At initial diagnosis: anti-HAV; if negative, HAV vaccine series should be administered.",
"        </p>",
"        <p>",
"         At initial diagnosis: anti-HBs, HBsAg, anti-HBc; if all are negative, hepatitis B vaccine series should be administered",
"         <sup>",
"          &Delta;",
"         </sup>",
"         .",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest radiograph",
"       </td>",
"       <td>",
"        As needed (eg, for symptoms and signs suggesting pulmonary disease or newly detected latent tuberculosis)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HIV: human immunodeficiency virus; RPR: rapid plasma reagin; VDRL: venereal disease research laboratory; MSM: men who have sex with men; PAP: Papanicolaou smear; TST: tuberculin skin test; IGRA: interferon-gamma release assay; anti-HCV: hepatitis C antibody; anti-HAV: hepatitis A virus antibody; anti-HBs: hepatitis B surface antibody; HBsAg: hepatitis B surface antigen; anti-HBc: hepatitis B core antibody.",
"     <br>",
"      * Testing should be performed among HIV positive adolescents who are sexually active; it is not necessary among HIV positive adolescents with vertical transmission until they become sexually active.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       In contrast to the recommendations for healthy adolescents, sexually active HIV-infected female adolescents should undergo cervical cancer screening after the onset of sexual activity and not wait until 21 years of age.",
"       <br>",
"        &Delta; If anti-HBs negative after the first vaccination series, the series may be repeated once. Patients with negative anti-HBs who are not hepatitis B infected (anti-HBc negative and HBsAg negative) should be monitored annually or if symptomatic for new infection.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Kaplan JE, Benson C, Holmes KH, et al. Guidelines for prevention and treatment of opportunistic infections in HIV-infected adults and adolescents: recommendations from CDC, the National Institutes of Health, and the HIV Medicine Association of the Infectious Diseases Society of America. MMWR Recomm Rep 2009; 58:1. Available at:",
"        <a href=\"file://aidsinfo.nih.gov/contentfiles/Adult_OI_041009.pdf\" target=\"_blank\">",
"         file://aidsinfo.nih.gov/contentfiles/Adult_OI_041009.pdf",
"        </a>",
"        . (Accessed on August 22, 2011).",
"       </li>",
"       <li>",
"        Panel on antiretroviral guidelines for adults and adolescents. Guidelines for the use of antiretroviral agents in HIV-1 infected adults and adolescents. Department of Health and Human Services. Available at:",
"        <a href=\"file://aidsinfo.nih.gov/contentfiles/AdultandAdolescentGL.pdf\" target=\"_blank\">",
"         file://aidsinfo.nih.gov/contentfiles/AdultandAdolescentGL.pdf",
"        </a>",
"        . (Accessed on August 22, 2011).",
"       </li>",
"       <li>",
"        ACOG Practice Bulletin No. 117: Gynecologic care for women with human immunodeficiency virus. Obstet Gynecol 2010; 116:1492.",
"       </li>",
"       <li>",
"        ACOG Committee Opinion No. 463: Cervical cancer in adolescents: screening, evaluation, and management. Obstet Gynecol 2010; 116:469.",
"       </li>",
"       <li>",
"        New York State Department of Health Guidelines. Available at:",
"        <a href=\"file://www.natap.org/2010/HIV/032510_01.htm\" target=\"_blank\">",
"         file://www.natap.org/2010/HIV/032510_01.htm",
"        </a>",
"        . (Accessed on August 22, 2011).",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
