" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Retinopathy of prematurity",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/32/10762/contributors\">",
