var title_f10_32_10765="Genetic mimics of vascular or prenatal insults";
var content_f10_32_10765=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F75778&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F75778&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Genetic conditions associated with microcephaly and neuroradiologic abnormalities mimicking vascular damage or prenatal infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Genetic condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Phenocopy*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Selected reference(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aicardi-Goutier&egrave;s syndrome (also known as pseudo-TORCH and allelic with Cree encephalitis (at least 5 genes, common))",
"       </td>",
"       <td>",
"        Congenital infection with thrombocytopenia, increased production of alpha-interferon, ulcerative skin lesions",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pseudo-TORCH syndrome type b",
"       </td>",
"       <td>",
"        Distinct pattern of band-like intracranial calcification, simplified gyration and polymicrogyria; severe postnatal microcephaly, seizures, developmental arrest",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sulphite oxidase deficiency",
"       </td>",
"       <td>",
"        Neonatal encephalopathy; neuroradiology and pathology can mimic perinatal asphyxia",
"       </td>",
"       <td>",
"        3, 4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mitochondrial/respiratory chain abnormality",
"       </td>",
"       <td>",
"        Disrupted brain development with congenital infection-like calcifications, and a complex neuronal migration disorder",
"       </td>",
"       <td>",
"        5, 6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PEHO-like syndrome",
"       </td>",
"       <td>",
"        Encephalopathy with hypoxic ischemic changes on MRI",
"       </td>",
"       <td>",
"        7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fetal brain disruption sequence",
"       </td>",
"       <td>",
"        Traumatic brain destruction",
"       </td>",
"       <td>",
"        8, 9",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cystic leukoencephalopathy",
"       </td>",
"       <td>",
"        Congenital CMV clinically and neuroradiologically",
"       </td>",
"       <td>",
"        10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bilateral porencephaly; cerebellar or vermis hypoplasia or aplasia; congenital heart defect",
"       </td>",
"       <td>",
"        Bilateral porencephaly (\"basket brain\") may resemble middle cerebral artery infarct",
"       </td>",
"       <td>",
"        11",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Phenocopy: a nongenetic condition with an appearance that is similar to that caused by a specific genotype.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Stephenson, JB. Aicardi-Goutier&egrave;s syndrome (AGS). Eur J Paediatr Neurol 2008; 12:355.",
"       </li>",
"       <li>",
"        Briggs, TA, Wolf, NI, D'Arrigo, S, et al. Band-like intracranial calcification with simplified gyration and polymicrogyria: a distinct \"pseudo-TORCH\" phenotype. Am J Med Genet A 2008; 146A:3173.",
"       </li>",
"       <li>",
"        Hobson, EE, Thomas, S, Crofton, PM, et al. Isolated sulphite oxidase deficiency mimics the features of hypoxic ischaemic encephalopathy. Eur J Pediatr 2005; 164:655.",
"       </li>",
"       <li>",
"        Hoffmann, C, Ben-Zeev, B, Anikster, Y, et al. Magnetic resonance imaging and magnetic resonance spectroscopy in isolated sulfite oxidase deficiency. J Child Neurol 2007; 22:1214.",
"       </li>",
"       <li>",
"        van Straaten, HL, van Tintelen, JP, Trijbels, JM, et al. Neonatal lactic acidosis, complex I/IV deficiency, and fetal cerebral disruption. Neuropediatrics 2005; 36:193.",
"       </li>",
"       <li>",
"        Samson, JF, Barth, PG, de Vries, JI, et al. Familial mitochondrial encephalopathy with fetal ultrasonographic ventriculomegaly and intracerebral calcifications. Eur J Pediatr 1994; 153:510.",
"       </li>",
"       <li>",
"        Longman, C, Tolmie, J, McWilliam, R, MacLennan, A. Cranial magnetic resonance imaging mistakenly suggests prenatal ischaemia in PEHO-like syndrome. Clin Dysmorphol 2003; 12:133.",
"       </li>",
"       <li>",
"        Schram, A, Kroes, HY, Sollie, K, et al. Hereditary fetal brain degeneration resembling fetal brain disruption sequence in two sibships. Am J Med Genet A 2004; 127A:172.",
"       </li>",
"       <li>",
"        Behunova, J, Zavadilikova, E, Bozoglu, TM, et al. Familial microhydranencephaly, a family that does not map to 16p13.13-p12.2:: relationship with hereditary fetal brain degeneration and fetal brain disruption sequence. Clin Dysmorphol 2010; 19:107.",
"       </li>",
"       <li>",
"        Henneke, M, Diekmann, S, Ohlenbusch, A, et al. RNASET2-deficient cystic leukoencephalopathy resembles congenital cytomegalovirus brain infection. Nat Genet 2009; 41:773.",
"       </li>",
"       <li>",
"        Bonnemann, CG, Meinecke, P. Bilateral porencephaly, cerebellar hypoplasia, and internal malformations: two siblings representing a probably new autosomal recessive entity. Am J Med Genet 1996; 63:428.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
