" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Embolism from aortic plaque: Thromboembolism",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/35/10809/contributors\">",
"     Author",
"    </a>",
