" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of craniosynostosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/37/10841/contributors\">",
