var title_f10_4_10316="Criteria for acute kidney injury";
var content_f10_4_10316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F83168&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F83168&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for acute kidney injury",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Serum creatinine criteria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Urine output criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        RIFLE",
"       </td>",
"       <td>",
"        Definition",
"       </td>",
"       <td>",
"        Increase in SCr by &ge;1.5 times baseline within seven days",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Staging",
"       </td>",
"       <td>",
"        R (Risk): Increase in SCr by 1.5 - &lt;2.0 times baseline",
"       </td>",
"       <td>",
"        UO &lt;0.5 mL/kg/h x 6 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        I (Injury): Increase in SCr by 2.0 - &lt;3.0 times baseline",
"       </td>",
"       <td>",
"        UO &lt;0.5 mL/kg/h x 12 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        F (Failure): Increase in SCr by &ge;3.0 times baseline",
"       </td>",
"       <td>",
"        UO &lt;0.3 mL/kg/h x 24 hr or anuria x 12 hrs",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        AKIN",
"       </td>",
"       <td>",
"        Definition",
"       </td>",
"       <td>",
"        Increase in SCr by 0.3 mg/dL or &ge;1.5 times baseline within 48 hours",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Staging",
"       </td>",
"       <td>",
"        1: Increase in SCr by &ge;0.3 mg/dL or &ge;1.5 - &lt;2.0 times baseline",
"       </td>",
"       <td>",
"        Less than 0.5 mL/kg per hour for more than 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2: Increase in SCr by &ge;2.0 - &lt;3.0 times baseline",
"       </td>",
"       <td>",
"        Less than 0.5 mL/kg per hour for more than 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3: Increase in SCr by &ge;3.0 times baseline",
"       </td>",
"       <td>",
"        Less than 0.3 mL/kg per hour for 24 hours or anuria for 12 hours",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Bellomo R, Ronco C, Kellum JA, et al. Acute renal failure-definition, outcome measures, animal models, fluid therapy and information technology needs: the Second International Consensus Conference of the Acute Dialysis Quality Initiative (ADQI) Group. Crit Care 2004; 8:B204. Copyright &copy; 2004 BioMed Central Ltd.",
"      </li>",
"      <li>",
"       Mehta RL, Kellum JA, Shah SV, et al. Acute Kidney Injury Network: report of an initiative to improve outcomes in acute kidney injury. Crit Care 2007; 11:R31. Copyright &copy; 2007 BioMed Central Ltd.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
