var title_f10_44_10955="Diagnostic criteria NF1";
var content_f10_44_10955=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F63696&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F63696&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    NIH diagnostic criteria for neurofibromatosis type 1",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Two or more of the following clinical features must be present:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Six or more caf&eacute; au lait macules of more than 5 mm in greatest diameter in prepubertal individuals, and more than 15 mm in greatest diameter in postpubertal individuals.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Two or more neurofibromas of any type or one plexiform neurofibroma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Freckling in the axillary or inguinal regions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Optic glioma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Two or more iris hamartoma (Lisch nodules)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Distinctive bony lesion such as sphenoid dysplasia, or thinning of the long bone cortex with or without pseudoarthrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A first-degree relative (parent, sibling, or offspring) with NF 1 based on the above criteria.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
