var title_f10_44_10956="ACC AHA CABG asx CAD mild AP";
var content_f10_44_10956=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F69950&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F69950&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA Guideline Summary: Coronary artery bypass graft surgery (CABG) in patients with asymptomatic ischemia or mild angina",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence",
"and/or general agreement that CABG should be performed in patients with",
"asymptomatic ischemia or mild angina in the following settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Significant left main coronary artery disease.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Significant left main equivalent disease, which is defined as &ge;70 percent stenosis of the proximal left anterior descending (LAD) and proximal left circumflex arteries.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Three-vessel disease; the survival benefit is greater with a reduced left ventricular ejection fraction (less than 50 percent) and/or a large area of myocardium at risk on noninvasive testing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Significant proximal",
"LAD disease with one- or two-vessel disease with a reduced left",
"ventricular ejection fraction (less than 50 percent) and/or a large",
"area of myocardium at risk on noninvasive testing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; One or two-vessel disease not involving the proximal LAD with a large area of myocardium at risk and high-risk criteria on noninvasive testing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa - The weight of",
"evidence or opinion is in favor of benefit from CABG in patients with",
"asymptomatic ischemia or mild angina in the following setting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Significant proximal LAD disease with one- or two-vessel disease in the absence of the above high-risk features.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIb - The evidence or",
"opinion is less well established for the use of CABG in patients with",
"asymptomatic ischemia or mild angina in the following setting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; One or two-vessel disease not involving the proximal LAD in the absence of the above high-risk features.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Eagle KA, Guyton RA, Davidoff R, et al. ACC/AHA 2004 guideline update for coronary artery bypass graft surgery: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Committee to Update the 1999 Guidelines for Coronary Artery Bypass Graft Surgery). Circulation 2004; 110:e340.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
