" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Childhood lead poisoning: Exposure and prevention",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/47/11001/contributors\">",
