var title_f10_48_11021="Prophylactic medication CVS";
var content_f10_48_11021=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F68579&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F68579&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Prophylactic or preventive medications* in CVS",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Children 2 to 5 years old",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Antihistamines: cyproheptadine (first choice) and pizotifen (available in UK, Canada)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cyproheptadine 0.25-0.5 mg/kg/day orally divided twice daily or three times daily (maximum 12 mg per 24 hours). (Available in 2 mg/5 mL liquid).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Side effects: increased appetite, weight gain, sedation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alternatives: pizotifen (available in UK, Canada)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Beta-blockers: propranolol (second choice)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Propranolol 0.25-1 mg/kg/day orally divided twice daily or three times daily, most often 10 mg twice daily or three times daily. Initiate at 0.25 mg/kg/day and increase every 1-4 weeks in increments of 0.25 mg/kg/day as needed and tolerated. (&lt;35 kg maximum 60 mg per 24 hours. &ge;35 kg maximum 120 mg per 24 hours). (Available in liquid).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Monitor: resting heart rate maintain &ge;60 bpm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Side effects: lethargy, reduced exercise intolerance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Contraindications: asthma, diabetes, heart disease, depression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Discontinuation: tapered for 1-2 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Children older than 5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Tricyclic antidepressants: amitriptyline (first choice)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Amitriptyline begin at 0.25-0.5 mg/kg/day orally at bedtime, increase every 1-4 weeks as needed and tolerated by 5-10 mg, until 1-1.5 mg/kg/day (maximum 75 mg per 24 hours). (For dose &gt;1 mg/kg/day, divide twice daily).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Monitor: &radic;EKG QT",
"        <sub>",
"         c",
"        </sub>",
"        interval before starting and 10 days after peak dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Side effects: constipation, sedation, arrhythmia, behavioral changes (especially in young children)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alternatives: nortriptyline",
"        <sup>",
"         &bull;",
"        </sup>",
"        (available in 10 mg/5 mL liquid)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Beta-blockers: propranolol (second choice) - see above",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Other agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Anticonvulsants: phenobarbital",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Phenobarbital 2 mg/kg/day orally administered at bedtime (maximum 40 mg per 24 hours)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Side effects: sedation, cognitive impairment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alternatives: topiramate, valproic acid, gabapentin, levetiracetam - consult Pediatric neurologist",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Supplements",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        L-carnitine 50-100 mg/kg/day orally divided twice daily or three times daily (maximum 1 g three times daily)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Coenzyme Q",
"        <sub>",
"         10",
"        </sub>",
"        10 mg/kg/day orally divided twice daily or three times daily (maximum 100 mg three times daily)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Side effects: diarrhea, fishy body odor (for L-carnitine)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * All medication recommendations are made for off-label use.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Limited or no published experience regarding efficacy.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Li BU, Lefevre F, Chelimsky GG, et al. North American Society for Pediatric Gastroenterology, Hepatology, and Nutrition consensus statement on the diagnosis and management of cyclic vomiting syndrome. J Pediatr Gastroenterol Nutr 2008; 47:379. Copyright &copy; 2008 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
