" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical pathological cases in gastroenterology: Esophagus",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/55/11124/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/55/11124/contributors\">",
"     Andres Gelrud, MD, MMSc",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/55/11124/contributors\">",
"     Deputy Editor",
"    </a>",
