var title_f10_55_11131="Procedure for sucrose analgesia";
var content_f10_55_11131=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F76111&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F76111&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Procedure for sucrose analgesia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Indications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Painful procedure such as venipuncture or lumbar puncture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Age &lt; 6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infant is restricted from oral intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Procedure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Use a 25 percent sucrose solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Administer 2 mL, perferably by allowing the infant to suck on a pacifier, or with a syringe, 1 mL in each cheek",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Administer no more than 2 minutes before beginning painful procedure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. May repeat the dose but not to exceed two doses in one hour",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
