" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   HLA matching and graft survival in kidney transplantation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/57/11159/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/57/11159/contributors\">",
"     Daniel C Brennan, MD, FACP",
"    </a>",
"    <br/>",
"   </div>",
