" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Recurrent pericarditis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/58/11177/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/58/11177/contributors\">",
"     Yehuda Adler, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?10/58/11177/contributors\">",
"     Massimo Imazio, MD, FESC",
