" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Oral antibiotic therapy of renal or perinephric abscess",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Antibiotic",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dose, interval",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Levofloxacin",
"      </td>",
"      <td>",
"       500 to 750 mg every 24 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ciprofloxacin",
"      </td>",
"      <td>",
"       500 mg every 12 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ciprofloxacin XR",
"      </td>",
"      <td>",
"       1000 mg every 24 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Trimethoprim-sulfamethoxazole",
"      </td>",
"      <td>",
"       1 double strength tablet every 12 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Trimethoprim",
"      </td>",
"      <td>",
"       100 mg every 12 hours or 200 mg every 24 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cefpodoxime",
"      </td>",
"      <td>",
"       200 mg every 12 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cefixime",
"      </td>",
"      <td>",
"       200 mg every 12 hours or 400 mg every 24 hours",
"      </td>",
