" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Carbon monoxide poisoning",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?10/6/10345/contributors\">",
