var title_f10_6_10349="Medications for malabsorption";
var content_f10_6_10349=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F51385&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F51385&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Medications used in malabsorption and maldigestion (adult)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Medication/dosage",
"      </td>",
"      <td class=\"subtitle1\">",
"       Comment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"2\">",
"       Antidiarrheal agents",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Loperamide 2-4 mg as needed, not to exceed 12 mg/day",
"      </td>",
"      <td class=\"sublist_other\">",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Diphenoxylate with atropine (Lomotil&reg;) 1-2 tabs after loose stool, not to exceed 8 per day",
"      </td>",
"      <td class=\"sublist_other\">",
"       1 tab = 2.5 mg diphenoxylate component",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Deodorized opium tincture 10 percent (10 mg per mL) 0.3 to 0.6 mL three times daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       Note: Highly concentrated. Contains 25 times more morphine than paregoric. Abrupt discontinuation may cause withdrawal symptoms.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"2\">",
"       Bile acid binding resins",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Cholestyramine 4 g three times daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       Administer &ge;1 hour before or &gt;4 hours after other drugs to prevent decreased absorption of other drug(s)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Colestipol granules 5-10 g three times daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       Administer &ge;1 hour before or &gt;4 hours after other drugs to prevent decreased absorption of other drug(s)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Pancreatic enzymes*",
"      </td>",
"      <td class=\"sublist_other_start\">",
"       General: Approximately 30,000 units (lipase component) with each meal",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Pancrelipase delayed-release capsules (Creon minimicrospheres)",
"      </td>",
"      <td class=\"sublist_other\">",
"       Enteric-coated micro pellets may be sprinkled on soft acidic foods",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Pancrelipase tablets and powder (Viokase, others) 1 g (equivalent to 20,000 units lipase component) with meals",
"      </td>",
"      <td class=\"sublist_other\">",
"       Effect improved if given with acid-suppressing drugs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"2\">",
"       Vitamins and minerals&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Vitamin A 40,000-50,000 units twice daily during acute repletion",
"      </td>",
"      <td class=\"sublist_other\">",
"       Maintenance: 8000-20,000 units/day (Dosage &ge;15,000 units can be teratogenic)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Vitamin D3 (cholecalciferol) 30,000-50,000 units/day",
"      </td>",
"      <td class=\"sublist_other\">",
"       Monitor serum calcium",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Vitamin K 2.5-12.5 mg daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Folic acid 5 mg/day during repletion",
"      </td>",
"      <td class=\"sublist_other\">",
"       Maintenance: 1 mg/day",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Vitamin B12 (cyanocobalamin) 1 mg subcutaneously (should be repeated x three over first week of repletion)",
"      </td>",
"      <td class=\"sublist_other\">",
"       Maintenance: 100 mcg q 1-2 months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Calcium carbonate 500 mg (elemental calcium) twice daily",
"      </td>",
"      <td>",
"       Monitor serum calcium",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Magnesium gluconate 1-4 g (54-216 mg elemental magnesium) four times daily",
"      </td>",
"      <td>",
"       Often produces diarrhea",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ferrous sulfate&Delta; 325 mg (65 mg elemental iron) three times daily",
"      </td>",
"      <td>",
"       Dose reduce if gastrointestinal symptoms occur",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     All dosages are oral, if not otherwise stated.",
"     <br>",
"      * Dose titration may be needed.",
"      <br>",
"       &bull; Maintenance doses for fat-soluble vitamins are highly dependent upon the degree of ongoing malabsorption. Blood levels should be checked every one to two years.",
"       <br>",
"        &Delta; Available as a solution. Equivalent dosage mixed in 8 ounces orange juice will greatly enhance absorptive efficiency due to vitamin C content of juice.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Modified from: Heimburger DC, Weinsier RL. Gastrointestinal and liver diseases, In: Handbook of Clinical Nutrition, 3rd ed, Heimburger DC, Weinsier RL (Eds), Mosby, St Louis 1997. p.424.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
