var title_f11_10_11436="Migratory skin lesions";
var content_f11_10_11436=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F71472&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F71472&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Infections associated with migratory skin lesions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Infection",
"       </td>",
"       <td class=\"subtitle1\">",
"        Geographic distribution",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cutaneous larva migrans",
"       </td>",
"       <td>",
"        Worldwide",
"       </td>",
"       <td>",
"        Creeping eruption",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dracunculiasis",
"       </td>",
"       <td>",
"        Focal in sub-Saharan Africa",
"       </td>",
"       <td>",
"        Movement of worm just before eruption",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fascioliasis",
"       </td>",
"       <td>",
"        Worldwide in areas where sheep and cattle are raised",
"       </td>",
"       <td>",
"        Migratory areas of inflammation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gnathostomiasis",
"       </td>",
"       <td>",
"        Most common in Asia but also found in other areas",
"       </td>",
"       <td>",
"        Migratory inflammatory subcutaneous swellings; urticaria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hookworm",
"       </td>",
"       <td>",
"        Worldwide; most common in tropical, subtropical areas&nbsp;with poor sanitation",
"       </td>",
"       <td>",
"        Itchy tracts at points of penetration of larvae through skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Loiasis",
"       </td>",
"       <td>",
"        Central and western Africa",
"       </td>",
"       <td>",
"        Migratory inflammatory swellings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paragonimiasis",
"       </td>",
"       <td>",
"        Most common in east and SE Asia, Africa",
"       </td>",
"       <td>",
"        Migratory swelling or subcutaneous nodules",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sparganosis",
"       </td>",
"       <td>",
"        Parasite found on all continents; most human cases from SE Asia",
"       </td>",
"       <td>",
"        Edematous, painful migratory swellings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Strongyloidiasis",
"       </td>",
"       <td>",
"        Most common in tropical, subtropical areas",
"       </td>",
"       <td>",
"        Itchy, papular and migratory serpiginous lesions (larva currens)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myiasis",
"       </td>",
"       <td>",
"        Dominant Diptera species vary with region",
"       </td>",
"       <td>",
"        Visible movement of maggot(s) within lesion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
