" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Postmenopausal hormone therapy and the risk of breast cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/11/11449/contributors\">",
"     Author",
"    </a>",
