var title_f11_12_11468="Arsenic chelating agents";
var content_f11_12_11468=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F69443&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F69443&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Adverse effects of chelating agents used for arsenic poisoning",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Chelating agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Adverse effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        BAL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endpoint: 24-hour urinary arsenic &lt;50 &micro;g/L or until another agent is substituted",
"       </td>",
"       <td>",
"        <p>",
"         Hypertension",
"        </p>",
"        <p>",
"         Febrile reaction, diaphoresis",
"        </p>",
"        <p>",
"         Nausea, vomiting, salivation",
"        </p>",
"        <p>",
"         Lacrimation, rhinorrhea",
"        </p>",
"        <p>",
"         Headache",
"        </p>",
"        <p>",
"         Painful injection, injection site sterile abscess",
"        </p>",
"        <p>",
"         Hemolysis in G6PD-deficient patients",
"        </p>",
"        <p>",
"         Chelation of essential metals (prolonged course)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Succimer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endpoint: 24-hour urinary arsenic &lt;50 &micro;g/L",
"       </td>",
"       <td>",
"        <p>",
"         Nausea, vomiting, diarrhea",
"        </p>",
"        <p>",
"         Abdominal gas, pain",
"        </p>",
"        <p>",
"         Transient elevations in hepatic amino transferases, and alkaline phosphatase",
"        </p>",
"        <p>",
"         Rash, pruritus",
"        </p>",
"        <p>",
"         Sore throat, rhinorrhea",
"        </p>",
"        <p>",
"         Drowsiness, paresthesias",
"        </p>",
"        <p>",
"         Thrombocytosis, eosinophilia",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        DMPS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endpoint: 24 hour urinary arsenic&lt;50 &micro;g/L",
"       </td>",
"       <td>",
"        <p>",
"         Allergic reactions",
"        </p>",
"        <p>",
"         Increased copper and zinc excretion",
"        </p>",
"        <p>",
"         Nausea",
"        </p>",
"        <p>",
"         Pruritus",
"        </p>",
"        <p>",
"         Vertigo",
"        </p>",
"        <p>",
"         Weakness",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Ford MD. Arsenic. In: Goldfrank's Toxicological Emergencies, Goldfrank L, Flomenbaum N, Howland MA, Hoffman R, Nelson L (Eds), Mcgraw-Hill, New York 2002. p.1183. Copyright &copy; 2002 McGraw Hill.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f11_12_11468=[""].join("\n");
