var title_f11_12_11469="Education at initial visit";
var content_f11_12_11469=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F56749&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F56749&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Delivery of asthma education by clinicians during patient care visits: recommendations for initial visit",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Assessment questions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Focus on:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Concerns",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Quality of life",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Expectations",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Goals of treatment",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Ask relevant questions from previous visit and also ask:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            \"What worries you most about your asthma?\"",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            \"What do you want to accomplish at this visit?\"",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            \"What do you want to be able to do that you can't do now because of your asthma?\"",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            \"What do you expect from treatment?\"",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            \"What medications have you tried?\"",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            \"What other questions do you have for me today?\"",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Information",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Teach in simple language:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            What is asthma?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            A chronic lung disease. The airways are very sensitive. They become inflamed and narrow; breathing becomes difficult.",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            Asthma treatments: two types of medicines are needed:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Long-term control: medications that prevent symptoms, often by reducing inflammation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Quick relief: short-acting bronchodilator relaxes muscles around airways",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Bring all medications to every appointment.",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"1\" rowspan=\"2\">",
"            When to seek medical advice. Provide appropriate telephone number.",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Skills",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Teach and demonstrate:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inhaler and spacer/holding chamber use. Check performance.",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            Self-monitoring skills that are tied to an action plan:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Recognize intensity and frequency of asthma symptoms",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Review the signs of deterioration and the need to reevaluate therapy:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist3\">",
"            Waking at night with asthma",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist3\">",
"            Increased medication use",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist3\">",
"            Decreased activity tolerance",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Use of a simple, written self-management plan",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
