" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Care of the uncircumcised penis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/18/11559/contributors\">",
"     Author",
