var title_f11_2_11308="Diagnostic criteria paroxysmal hemicrania";
var content_f11_2_11308=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F58505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F58505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for paroxysmal hemicrania",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        A. At least 20 attacks fulfilling B-D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Severe unilateral orbital, supraorbital, or temporal pain lasting 2 to 30 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        C. Headache is accompanied by at least one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1. Ipsilateral conjunctival injection and/or lacrimation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2. Ipsilateral nasal congestion and/or rhinorrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        3. Forehead and facial sweating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        4. Ipsilateral eyelid edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        5. Ipsilateral forehead and facial sweating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        6. Ipsilateral miosis and/or ptosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D. Attacks have a frequency &gt;5 per day for more than half the time, although periods with lower frequency may occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. Attacks are prevented completely by therapeutic doses of indomethacin*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        F. Not attributed to another disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Episodic paroxysmal headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Description: occurs in periods lasting seven days to one year separated by pain free periods lasting one month or more",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Chronic paroxysmal headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Description: attacks occur for more than one year without remission or with remissions lasting less than one month",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * To rule out an incomplete response, indomethacin should be used in a dose of &ge;150 mg daily orally or rectally, or &ge;100 mg by injection. Smaller doses are often sufficient for maintenance.",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted by permission of Sage: Headache classification subcommittee of the International Headache Society. The International Classification of Headache Disorders: 2nd edition. Cephalalgia 2004; 24 Suppl 1:9. Copyright &copy; 2004 Sage Publications.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
