var title_f11_2_11309="Evaluation of child IBD";
var content_f11_2_11309=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F77827&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F77827&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of the child with suspected inflammatory bowel disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        History",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abdominal pain, appetite",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stool frequency, consistency, rectal bleeding, nocturnal diarrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Family history: relatives with inflammatory bowel disease, familial growth patterns",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Previous growth data*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        School attendance and daily activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Psychosocial history, including impact upon daily life of patient and parent(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Physical examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Height and weight, evaluating for trends and growth velocity*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abdominal examination: tenderness, mass",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rectal examination: evaluate for perianal disease and occult blood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rash, arthritis, clubbing, oral lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sexual maturity staging (Tanner stage)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Laboratory tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CBC with differential count, platelets, ESR, C-reactive protein",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum total protein, albumin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stool guaiac (hemoccult), leukocytes, calprotectin; urine analysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stool bacterial culture, smears for ova and parasite, C. difficile toxin assay",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tuberculosis screening (tuberculin skin test)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Additional tests as indicated, depending upon clinical findings:",
"        </p>",
"        <ul>",
"         <li>",
"          To further characterize the type of inflammatory bowel disease: P-ANCA, ASCA",
"         </li>",
"         <li>",
"          To investigate other causes of diarrhea: lactose/glucose hydrogen breath test for lactose intolerance/bacterial overgrowth, 72-hour fecal fat quantitation, and stool alpha-1-antitrypsin",
"         </li>",
"         <li>",
"          If poor nutrition is suspected: serum iron, calcium, magnesium, folate, vitamins A, E, B12, and zinc*",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Radiographic studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         For localization of small bowel disease, one of the following:",
"        </p>",
"        <ul>",
"         <li>",
"          Upper GI series with small bowel follow-through",
"         </li>",
"         <li>",
"          Abdominal MRI with oral contrast",
"         </li>",
"         <li>",
"          Abdominal CT with oral contrast",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Additional tests as indicated, depending upon clinical findings: bone age*, abdominal plain films, fistulogram, ultrasound or HMPAO-labeled white blood cell scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Endoscopic studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colonoscopy (including ileoscopy) with biopsies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upper endoscopy with biopsies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Additional procedures as indicated:",
"        </p>",
"        <ul>",
"         <li>",
"          Video capsule endoscopy if more information on small bowel involvement is needed",
"         </li>",
"         <li>",
"          MRCP or ERCP if sclerosing cholangitis is suspected",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CBC: complete blood count; ESR: erythrocyte sedimentation rate; P-ANCA: perinuclear antineutrophil cytoplasmic antibodies (P-ANCA); ASCA: Anti-Saccharomyces cerevisiae antibodies; MRI: magnetic resonance imaging; CT: computerized tomography; HMPAO: hexamethylpropyleneamineoxime; MRCP: magnetic resonance cholangiopancreatography; ERCP: endoscopic retrograde cholangiopancreatography.",
"     <br/>",
"     * For a more detailed discussion, refer to UpToDate topic review on growth failure in children with inflammatory bowel disease.",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Shashidhar H, Integlia MJ, Grand RJ. Clinical manifestations of pediatric inflammatory bowel disease. In: Inflammatory Bowel Disease, Kirsner JB (Ed), WB Saunders, Philadelphia 2000. Copyright &copy; 2000 Elsevier Science.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
