" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Electrocardiogram in the diagnosis of myocardial ischemia and infarction",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/24/11658/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/24/11658/contributors\">",
"     Ary L Goldberger, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/24/11658/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/24/11658/contributors\">",
"     Freek Verheugt, MD, FACC, FESC",
"    </a>",
"    <br/>",
