" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of schwannomatosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/29/11731/contributors\">",
