var title_f11_29_11740="Dx criteria PCOS";
var content_f11_29_11740=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F80095&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F80095&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for polycystic ovary syndrome*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        National Institute of Health criteria (1990)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence of anovulation or oligo-ovulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Evidence of clinical or biochemical hyperandrogenism (either)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Clinical: hirsutism, acne, or male pattern balding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Biochemical: high serum androgen concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Rotterdam criteria by the European Society of Human Reproduction and Embryology/American Society of Reproductive Medicine (2003)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Two out of three following findings are required for diagnosis:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Evidence of anovulation or oligo-ovulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Evidence of clinical or biochemical hyperandrogenism (either)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Clinical: hirsutism, acne, or male pattern balding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Biochemical: high serum androgen concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A polycystic ovary (by ultrasound)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Androgen Excess and PCOS Society criteria (2006)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Evidence of ovarian dysfunction (either)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Evidence of anovulation or oligo-ovulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        A polycystic ovary (by ultrasound)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Evidence of clinical or biochemical hyperandrogenism (either)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Clinical: hirsutism, acne, or male pattern balding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Biochemical: high serum androgen concentration",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * All criteria involve exclusion of other causes of hyperandrogenism and menstrual irregularity.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
