var title_f11_31_11772="Seven-point checklist";
var content_f11_31_11772=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F60378&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F60378&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The seven-point checklist of dermoscopy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Criteria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Score*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        <strong>",
"         Major criteria",
"        </strong>",
"       </td>",
"       <td>",
"        Atypical pigment network",
"       </td>",
"       <td>",
"        Prominent (hyperpigmented or thick) and irregular network.",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        White-blue areas",
"       </td>",
"       <td>",
"        Irregular, structureless white-blue area with an overlying \"ground-glass\" film. It cannot occupy the entire lesion.",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Atypical vascular pattern",
"       </td>",
"       <td>",
"        Linear irregular or dotted vessels, irregularly distributed outside areas of regression.",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        <strong>",
"         Minor criteria",
"        </strong>",
"       </td>",
"       <td>",
"        Radial streaming (streaks)",
"       </td>",
"       <td>",
"        Radial and asymmetric linear or bulbous projections at the edge of the lesion.",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Irregular blotches",
"       </td>",
"       <td>",
"        Black, brown and/or gray structureless areas with irregular shape or distribution.",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Irregular dots and globules",
"       </td>",
"       <td>",
"        Black or brown round structures irregularly distributed.",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Regression structures",
"       </td>",
"       <td>",
"        White scar-like depigmentation and/or \"peppering\".",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    For the diagnosis of melanoma, a total score of 3 or more is required.",
"    <div class=\"footnotes\">",
"     * In the Revised 7-point checklist (Argenziano G, et al. Br J Dermatol 2011; 164:785), all scores have a value of 1 and the threshold for excision is 1 instead of 3.",
"    </div>",
"    <div class=\"reference\">",
"     Original table modified for this publication. Argenziano G, Soyer HP, Chimenti S, et al. Dermoscopy of pigmented skin lesions: Results of a consensus meeting via the Internet. J Am Acad Dermatol 2003; 48:679. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
