var title_f11_31_11773="Diagnostic criteria migraine";
var content_f11_31_11773=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F50876&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F50876&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    International Headache Society diagnostic criteria for migraine",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Migraine without aura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. At least five attacks fulfilling criteria B through D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Headache attacks lasting 4 to 72 hours (untreated or unsuccessfully treated)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        C. Headache has at least two of the following characteristics:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Unilateral location",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Pulsating quality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Moderate or severe pain intensity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Aggravation by or causing avoidance of routine physical activity (eg, walking or climbing stairs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        D. During headache at least one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Nausea, vomiting, or both",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Photophobia and phonophobia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. Not attributed to another disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Migraine with aura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. At least two attacks fulfilling criterion B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        B. Migraine aura fulfilling criteria B and C for one of the following subforms:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Typical aura with migraine headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Typical aura with nonmigraine headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Typical aura without headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Familial hemiplegic migraine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Sporadic hemiplegic migraine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Basilar-type migraine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. Not attributed to another disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Migraine with typical aura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. At least two attacks fulfilling criteria B through D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        B. Aura consisting of at least one of the following, but no motor weakness:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Fully reversible visual symptoms including positive (eg, flickering lights, spots, or lines) and/or negative features (ie, loss of vision)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Fully reversible sensory symptoms including positive (ie, pins and needles) and/or negative features (ie, numbness)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Fully reversible dysphasic speech disturbance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        C. At least two of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Homonymous visual and/or unilateral sensory symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        At least one aura symptom develops gradually over &ge;5 minutes and/or different aura symptoms occur in succession over &ge;5 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Each symptom lasts &ge;5 and &le;60 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D. Headache fulfilling criteria B through D for migraine without aura begins during the aura or follows aura within 60 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. Not attributed to another disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Features of migraine in children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Attacks may last 1 to 72 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Headache is commonly bilateral; an adult pattern of unilateral pain usually emerges in late adolescence or early adulthood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Occipital headache is rare and raises diagnostic caution for structural lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Photophobia and phonophobia may be inferred by behavior in young children",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Headache Classification Committee of the International Headache Society. The International Classification of Headache Disorders. Cephalalgia 2004; 24:1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
