var title_f11_32_11788="Secondary prophylaxis for rheumatic fever - Selection of therapy";
var content_f11_32_11788=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F68830&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F68830&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Secondary prophylaxis for rheumatic fever - Selection of therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Continuous regimen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Adults &gt;27 kg",
"       </td>",
"       <td class=\"subtitle2\">",
"        Children &le;27 kg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Penicillin G benzathine intramuscular (Bicillin LA)",
"       </td>",
"       <td>",
"        1.2 million units every 4 weeks*",
"       </td>",
"       <td>",
"        600,000 units every 4 weeks*",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Penicillin V oral",
"       </td>",
"       <td>",
"        250 mg orally twice daily",
"       </td>",
"       <td>",
"        250 mg orally twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sulfadiazine",
"       </td>",
"       <td>",
"        1000 mg orally once daily",
"       </td>",
"       <td>",
"        500 mg orally once daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Allergy to penicillin and sulfadiazine:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Azithromycin",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td class=\"sublist_other\">",
"        250 mg orally once daily",
"       </td>",
"       <td class=\"sublist_other\">",
"        5 mg/kg orally once daily (up to 250 mg)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In high-risk situations, administration every three weeks is justified and recommended.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     For small children and infants: 25,000 units per kg intramuscularly every 4 weeks or 3 weeks (high-risk).",
"     <br/>",
"     &Delta; Macrolide susceptibility testing should be pursued prior to use of this drug class. Erythromycin is an acceptable alternative to azithromycin, although the latter has fewer adverse effects and permits once daily dosing. Erythromycin dosing for adults: 250 mg orally twice daily. Dosing for children: 20 mg/kg/day divided twice daily (maximum 500 mg per day).",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Gerber MA, Baltimore RS, Eaton CB, et al. Prevention of Rheumatic Fever and Diagnosis and Treatment of Acute Streptococcal Pharyngitis: A Scientific Statement From the American Heart Association Rheumatic Fever, Endocarditis, and Kawasaki Disease Committee of the Council on Cardiovascular Disease in the Young, the Interdisciplinary Council on Functional Genomics and Translational Biology, and the Interdisciplinary Council on Quality of Care and Outcomes Research. Circulation 2009; 119(11):1541-51. Copyright &copy; 2009 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
