"   <div class=\"list fbnlist drugH1Div drugBrandNames\" id=\"F4304076\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     International Brand Names",
"    </span>",
"    <ul>",
"     <li>",
"      Cardomel (ES);",
"     </li>",
"     <li>",
"      Perfan (BE, DE, GB, IE, IT, NL);",
"     </li>",
"     <li>",
"      Perfane (FR, IT, LU)",
"     </li>",
"    </ul>",
"   </div>",
"   <div class=\"list_set htclist drugH1Div drugBrandNames\" id=\"F1978683\" xmlns=\"file://www.w3.org/1999/xhtml\">",
