var title_f11_37_11868="Temporary transvenous pacing indications";
var content_f11_37_11868=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F63850&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F63850&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for temporary transvenous pacing*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Asystole.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Symptomatic bradycardia (includes sinus bradycardia with hypotension and type I second-degree AV block with hypotension not responsive to atropine).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Bilateral BBB (alternating BBB, or RBBB with alternating LAFB/LPFB) (any age).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. New or indeterminate age bifascicular block (RBBB with LAFB or LPFB, or LBBB).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Mobitz type II second-degree AV block.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. RBBB and LAFB or LPFB (new or indeterminate).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. RBBB with first-degree AV block.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. LBBB, new or indeterminate.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Incessant VT, for atrial or ventricular overdrive pacing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Recurrent sinus pauses (greater than 3 seconds) not responsive to atropine.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIb",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Bifascicular block of indeterminate age.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. New or age-indeterminate isolated RBBB.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. First-degree heart block.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Type I second-degree AV block with normal hemodynamics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Accelerated idioventricular rhythm.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Bundle branch block known to exist before acute MI.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * It should be noted that in choosing a transvenous pacemaker system, patients with substantially depressed ventricular performance, including RV infarction, may respond better to atrial/AV sequential pacing than ventricular pacing.",
"    </div>",
"    <div class=\"reference\">",
"     Data from Ryan, TJ, Anderson, JL, Antman, EM, et al, JACC 1996; 28:1367-1368.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
