var title_f11_38_11884="Breast cancer genes";
var content_f11_38_11884=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F66808&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F66808&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Major genes associated with breast cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"15%\">",
"     </colgroup>",
"     <colgroup width=\"55%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Gene",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gene frequency",
"       </td>",
"       <td class=\"subtitle1\">",
"        Other cancers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BRCA1",
"       </td>",
"       <td>",
"        HBOC",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Ovary, fallopian tube, male breast, pancreas, prostate*, possibly cervix and uterus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BRCA2",
"       </td>",
"       <td>",
"        HBOC",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Ovary, fallopian tube, male breast, pancreas, prostate*, possibly biliary tract, stomach, and melanoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        p53",
"       </td>",
"       <td>",
"        Li-Fraumeni",
"       </td>",
"       <td>",
"        Very rare",
"       </td>",
"       <td>",
"        Soft tissue and bone sarcomas, leukemia, brain, adrenocortical malignancies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PTEN",
"       </td>",
"       <td>",
"        Cowden",
"       </td>",
"       <td>",
"        Very rare",
"       </td>",
"       <td>",
"        Hamartomas (papillomas of the lips and mucous membranes, acral skin keratoses, trichillemmomas of skin), uterus, non-medullary thyroid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CDH1",
"       </td>",
"       <td>",
"        Hereditary Diffuse Gastric Cancer Syndrome",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Lobular breast cancer, diffuse gastric cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        STK11",
"       </td>",
"       <td>",
"        Peutz-Jeghers",
"       </td>",
"       <td>",
"        Very rare",
"       </td>",
"       <td>",
"        Hamartomatous gastrointestinal tract polyps, small bowel, stomach, colorectal, pancreas, lung, uterus, ovary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PALB2",
"       </td>",
"       <td>",
"        Hereditary pancreatic cancer",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Pancreatic cancer, Fanconi anemia",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     GI: gastrointestinal; HBOC: hereditary breast ovarian cancer syndrome.",
"     <br/>",
"     * Men with BRAC2 mutations have a five- to sevenfold increased risk of prostate cancer; magnitude of risk is less clear with BRAC1.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
