var title_f11_43_11965="Burkitt therapy CALGB 9251";
var content_f11_43_11965=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F76650&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F76650&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Intensive treatment for Burkitt leukemia/lymphoma: CALGB 9251 (revised*)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cycle 1",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyclophosphamide: 200 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV per day on days 1 through 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prednisone: 60 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        PO per day on days 1 through 7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cycles 2, 4, and 6",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ifosfamide: 800 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        over one hour IV per day on days 1 through 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mesna: 200 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV per day on days 1 through 5 at 0, 4, and 8 hours after ifosfamide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Methotrexate: 150 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV over 30 minutes on day 1, then 1.35 g/m",
"        <sup>",
"         2",
"        </sup>",
"        IV over the next 23.5 hours, for a total dose of 1.5 g/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leucovorin: 50 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV 36 hours after initiation of methotrexate, then 15 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        every 6 hours until serum methotrexate concentration &lt;0.05 micromolar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vincristine: 2 mg IV push on day 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cytarabine: 150 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day by continuous IV infusion on days 4 and 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Etoposide: 80 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day IV over one hour on days 4 and 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dexamethasone: 10 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        PO per day on days 1 through 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cycles 3, 5, and 7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyclophosphamide: 200 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day IV on days 1 through 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Methotrexate: 150 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV over 30 minutes on day 1, then 1.35 g/m",
"        <sup>",
"         2",
"        </sup>",
"        IV over the next 23.5 hours, for a total dose of 1.5 g/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leucovorin: 50 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV 36 hours after initiation of methotrexate, then 15 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        every 6 hours until serum methotrexate concentration &lt;0.05 micromolar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vincristine: 2 mg IV push on day 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Doxorubicin: 25 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per day by IV bolus on days 4 and 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dexamethasone: 10 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        PO per day on days 1 through 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Intrathecal chemotherapy with cycles 2 through 7 (amended)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Methotrexate: 15 mg IT on day 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cytarabine: 40 mg IT on day 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hydrocortisone: 50 mg IT on day 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cranial irradiation (amended)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2400 cGy administered in 12 fractions after chemotherapy for cycle 7 completed, only for patients who had prior bone marrow involvement at the time of presentation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CALGB: Cancer and Leukemia Group B; IV: intravenously; PO: by mouth; IT: intrathecal injection.",
"     <br>",
"      * Revised protocol as given to Cohort 2 of the study in order to reduce the incidence and severity of neurotoxicity.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       All cycles are repeated at 21-day intervals except cycle 1, which lasts 7 days.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Modified from: Lee, EJ, et al. J Clin Oncol 2001; 19:4014 and Rizzieri, DA, et al. Cancer 2004; 100:1438.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
