" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations and diagnosis of small intestinal bacterial overgrowth",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/45/11990/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/45/11990/contributors\">",
"     Jon A Vanderhoof, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/45/11990/contributors\">",
"     Rosemary J Pauley-Hunter, NP-C, MS, RN",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/45/11990/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/45/11990/contributors\">",
"     J Thomas LaMont, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/45/11990/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/45/11990/contributors\">",
"     Shilpa Grover, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
