var title_f11_47_12028="Major complications of Shigella";
var content_f11_47_12028=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F69134&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F69134&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Major complications of Shigella infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Complication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prevalence, percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Intestinal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Proctitis or rectal prolapse",
"       </td>",
"       <td>",
"        *",
"        <br/>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Toxic megacolon",
"       </td>",
"       <td>",
"        3.0",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intestinal obstruction",
"       </td>",
"       <td>",
"        2.5",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Colonic perforation",
"       </td>",
"       <td>",
"        1.0",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Systemic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bacteremia",
"       </td>",
"       <td>",
"        4.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Moderate to severe hypovolemia",
"       </td>",
"       <td>",
"        10-12.0",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hyponatremia",
"       </td>",
"       <td>",
"        29.0",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leukemoid reaction",
"       </td>",
"       <td>",
"        3.0",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Neurologic symptoms",
"       </td>",
"       <td>",
"        12-45.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Reactive arthritis or Reiter's syndrome",
"       </td>",
"       <td>",
"        1.4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hemolytic-uremic syndrome",
"       </td>",
"       <td>",
"        &lt;1.0",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Unknown.",
"     <br>",
"      &bull; Prevalence rate has been reported for a subpopulation of individuals with",
"      <em>",
"       Shigella",
"      </em>",
"      enteritis, and therefore the reported prevalence probably represents an overestimation of the true prevalence in all individuals with the disease.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
