"  <div id=\"topicTitle\">",
"   Thin basement membrane nephropathy (benign familial hematuria)",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/51/12088/contributors\">",
"     Author",
"    </a>",
