var title_f11_53_12123="Clinical effects of blood alcohol concentration";
var content_f11_53_12123=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F64642&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F64642&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical effects of blood alcohol concentration",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Blood alcohol concentration",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        20-50 mg/dL (4.4-11 mmol/L)",
"       </td>",
"       <td>",
"        Diminished fine motor coordination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        50-100 mg/dL (11-22 mmol/L",
"       </td>",
"       <td>",
"        Impaired judgement; impaired coordination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        100-150 mg/dL (22-33 mmol/L)",
"       </td>",
"       <td>",
"        Difficulty with gait and balance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        150-250 mg/dL (33-55 mmol/L)",
"       </td>",
"       <td>",
"        Lethargy; difficulty sitting upright without assistance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        300 mg/dL (66 mmol/L)",
"       </td>",
"       <td>",
"        Coma in the non-habituated drinker",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        400 mg/dL (88 mmol/L)",
"       </td>",
"       <td>",
"        Respiratory depression",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Marx JA. Rosen's emergency medicine: concepts and clinical practice, 5th ed, Mosby, Inc., St. Louis 2002. p. 2513. Copyright &copy; 2002 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
