var title_f11_53_12124="Etiology of rickets";
var content_f11_53_12124=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F67378&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F67378&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Etiology of rickets",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Deficient intake of minerals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inadequate calcium intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inadequate phosphate intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Poor absorption of minerals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin D deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin D dependent rickets type I (pseudovitamin D deficiency)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hereditary vitamin D resistant rickets (vitamin D dependent rickets type II)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High phytin content (eg, soy formula)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antacids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anticonvulsants (eg, phenytoin, phenobarbital)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fanconi syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatic insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fat malabsorption (eg, cystic fibrosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Increased excretion of minerals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Furosemide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Renal tubular dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Phosphaturia (eg, X-linked hypophosphatemic rickets, hereditary hypophosphatemic rickets with hypercalcuria, tumor-induced osteomalacia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Renal tubular acidosis with hypercalciuria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Renal tubular damage (eg, cystinosis, tyrosinosis, galactosemia, fructose intolerance, Wilson disease, lead poisoning, other heavy metal poisoning)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tumors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Local defect of bone cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypophosphatasia (alkaline phosphatase deficiency)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Bergstrom WH. Twenty ways to get rickets in the 1990's. Contemp Pediatr 1991; 8:88.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
